import React from 'react'
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
let settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    cssEase: "linear"
}
function Card(props) {
    const getCard = props.card
    return (
        <Slider {...settings}>
            <div className="card-wrapper">
                {getCard.map((items) =>
                    <div className="card">
                        <div className="card-image">
                            <img style={{ width: "100px", height: "100px" }} src={items.avatar} />
                        </div>
                        <div className="details">
                            <h2>{items.name}<span className="job-title">{items.gianguoilon}</span></h2>
                        </div>
                        <p>{items.gianguoilon}</p>
                    </div>
                )
                }
            </div>
        </Slider>
    )
}

export default Card
