import React, { useEffect, useState } from 'react'
import { message, Popconfirm, Spin, Table } from 'antd'
import { Link, useHistory, useRouteMatch } from 'react-router-dom';
import { Button } from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux';
import { hoadoncanhanData, updatehoadoncanhan } from '../Hoadoncanhan/hoadoncanhanSlice';
import { responsecommentData, updateresponsecomment } from './responsecommentSlice';
import Modal from 'antd/lib/modal/Modal';
// import { addthongbao } from './thongbaoSlice';
function Responsecomment() {
    const dispatch = useDispatch();
    const [isModalVisible, setIsModalVisible] = useState(false);
    const [state, setState] = useState({ giatien: "", luuy: "", hoadonId: "", userId: "" });
    const showModal = (e, e1) => {
        setIsModalVisible(true);
        setState({
            ...state,
            hoadonId: e,
            userId: e1
        })
    };
    const handleonchange = (e) => {
        setState({
            ...state,
            [e.target.name]: e.target.value
        })
    }
    const actionResult = async () => { await dispatch(responsecommentData()) }

    const handleOk = () => {
        // if (giatien !== "" && luuy !== "") {
        //     setIsModalVisible(false);
        //     dispatch(updateresponsecomment({ idsua: hoadonId, giatien: giatien, luuy: luuy, kiemduyet: 1 }))
        //     // dispatch(addthongbao({ userId: userId, noidung: "Tạo tour thành công bấm để xem chi tiết", status: 1 }))
        //     setTimeout(() => {
        //         actionResult();
        //     }, 500);
        // } else {
        //     message.warning("Thông tin chưa đầy đủ!")
        // }
    };

    const handleCancel = () => {
        setIsModalVisible(false);
    };
    const columns = [
        {
            title: 'Loại phân tích',
            dataIndex: 'user',
        },
        {
            title: 'Phản hồi',
            dataIndex: 'nxp',
        },
        {
            title: 'Kiểm duyệt',
            dataIndex: 'kd'
        }
    ];
    useEffect(() => {
        actionResult();
    }, [])
    const responsecomment = useSelector(state => state.responsecomments.responsecomment.data);
    
    const loading = useSelector(state => state.responsecomments.loading)

    // const { luuy, giatien, hoadonId, userId } = state
    return (
        <div id="admin">
            <div className="heading">
                <h4>Quản lý phản hồi khách hàng</h4>
                <div className="hr"></div>
            </div>
            <div className="content">
                {loading ? <div className="spin"><Spin className="mt-5" /></div> :
                    <Table columns={columns} dataSource={responsecomment.map((ok, index) => (
                        {
                            key: index + 1,
                            user: <span>{ok.analyzeComment}</span>,
                            nxp: <span>{ok.responseComment}</span>,
                            kd: <div className="action"><span onClick={() => { showModal(ok.id, ok.userId) }}><i class="fas fa-check text-danger"></i></span> </div>,
                        }))} />
                }
                <Modal title="Duyệt tour" visible={isModalVisible} onOk={handleOk} onCancel={handleCancel}>
                    <div class="form-group">
                        <label for="">Giá tiền</label>
                        <input type="number" min="0" className="form-control" name="giatien" value={giatien} onChange={handleonchange} id="" aria-describedby="helpId" placeholder="" />
                    </div>
                    <div class="form-group">
                        <label for="">Lưu ý</label>
                        <textarea className="form-control" name="luuy" value={luuy} onChange={handleonchange} id="" max="1000" cols="30" rows="4"></textarea>
                    </div>
                </Modal>
            </div>
        </div>
    )
}

Responsecomment.propTypes = {
}

export default Responsecomment
