import React, { useEffect } from "react";
import { Chart as ChartJS, ArcElement, Tooltip, Legend, BarElement, CategoryScale, LinearScale } from "chart.js";
import { Pie, Bar } from "react-chartjs-2";
import { useDispatch, useSelector } from 'react-redux';
import binhluanchudeApi from '../../../../api/binhluanchudeApi'
import chudeApi from '../../../../api/chudeApi'


ChartJS.register(ArcElement, Tooltip, Legend, BarElement, CategoryScale, LinearScale);
let binhluancd , chudes
const test = async () => {
  
  const testDT = await binhluanchudeApi.getallbinhluan();
  const testChude = await chudeApi.getallbinhluan();
  chudes = testChude.data
  binhluancd = testDT.data
}
test()
function Bieudo() {
  // const binhluanchudes = useSelector(state => state.binhluanchudes.binhluanchude.data)
  // const chudes = useSelector(state => state.chudes.chude.data)
  const binh = []
  binhluancd.forEach(binhluan => {
    if(binh.indexOf(binhluan) !== -1){
      binh.push(binhluan)
    }
  })
  const chude = chudes.map(chude=>chude.chuDe)
  const arr = []
  chude.forEach(chude => {
    const kq = binhluancd.filter(binhluan => binhluan.Chude.chuDe === chude)
    arr.push(kq);
  })
  const d = arr.map(itema => {
    const testb = itema.map(item=>item.binhluanId)
    let test = itema.filter((item,index)=> {
      return !testb.includes(item.binhluanId, index + 1)
    })
    return test
  })
  const k = d.map(item => item.length)
  const data = {
    labels: chude,
    datasets: [
      {
        label: "# of Votes",
        data: k,
        backgroundColor: [
          "rgba(255, 99, 132, 0.2)",
          "rgba(54, 162, 235, 0.2)",
          "rgba(255, 206, 86, 0.2)",
          "rgba(75, 192, 192, 0.2)",
          "rgba(153, 102, 255, 0.2)",
          "rgba(255, 159, 64, 0.2)",
        ],
        borderColor: [
          "rgba(255, 99, 132, 1)",
          "rgba(54, 162, 235, 1)",
          "rgba(255, 206, 86, 1)",
          "rgba(75, 192, 192, 1)",
          "rgba(153, 102, 255, 1)",
          "rgba(255, 159, 64, 1)",
        ],
        borderWidth: 1,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Chart.js Bar Chart',
      },
    },
  };
  // Process data chart positive and negative 
  const labels = chude;
  let blpositive = [];
  const blcdPostive = arr.map(blcd => blcd.filter(item=>item.Binhluan.analyzeComment === 'Positive'))
  console.log(blcdPostive)
  const data1 = {
    labels,
    datasets: [
      {
        label: 'Chu de',
        data: [12, 19, 3, 5, 2, 3],
        backgroundColor: 'rgba(255, 99, 132, 0.5)',
      }
    ],
  };
  const labels2 = ['January', 'February', 'March', 'April', 'May', 'June', 'July'];

  const data2 = {
    labels2,
    datasets: [
      {
        label: 'Dataset 1',
        data: [12, 19, 3, 5, 2, 3],
        backgroundColor: 'rgba(255, 99, 132, 0.5)',
      },
      {
        label: 'Dataset 2',
        data: [12, 19, 3, 5, 2, 3],
        backgroundColor: 'rgba(53, 162, 235, 0.5)',
      },
    ],
  };
  return (
    <div style={{ display: "flex" ,"flex-wrap": "wrap", "margin": "10px" }}>
      <div style={{ width: 300 }}>
        <Pie
          data={data}
          width={300}
          height={300}
          options={{ 
            plugins: {
              title: {
                display: true,
                text: 'Positive'
              }
            }
          }}
        />
        Chart 
      </div>
      <div style={{ width: 500  }}>
        <Bar
          data={data1}
          options={options}
        />
        Positive
      </div>
      <div style={{ width: 500  }}>
        <Bar
          data={data1}
          options={options}
        />
        Negative
      </div>
      <div style={{ width: 500  }}>
        <Bar
          data={data2}
          options={options}
        />
        Negative
      </div>
    </div>
  );
}

Bieudo.propTypes = {};

export default Bieudo;
