import React from 'react'
import { useSelector } from 'react-redux'
import { useParams } from 'react-router-dom';
import "./taikhoan.css"
function Chitiettaikhoan(props) {
    const { id } = useParams();
    const users = useSelector(state => state.taikhoan.user.data);
    const user = []
    if (users) {
        for (let i = 0; i < users.length; i++) {
            if (users[i].id === +id) {
                user.push(users[i])
            }
        }
    }
    console.log(user);

    return (
        <div id="admin">
            <div className="heading">
                <h4>Chi tiết tài khoản</h4>
                <div className="hr"></div>
            </div>
            <div className="content">
                <div className="ct">
                    {!user ? 's' :
                        user.map(ok => (
                            <div key={ok.id}>
                                <div className="text-center"><img src={ok.avatar ? ok.avatar : 'data:image/jpeg;base64,'} className="avatar-ct" alt="" /></div>
                                <p>Tên người dùng:&emsp; <b><i>{ok.name}</i></b></p>
                                <p>Email:&emsp; <b><i>{ok.email}</i></b></p>
                                <p>Số điện thoại:&emsp; <b><i></i>{ok.phone}</b></p>
                                <p>Giới tính:&emsp; <b><i>{ok.sex === 1 ? "Nam" : "Nữ"}</i></b></p>
                                <p>Ngày sinh:&emsp; <b><i>{ok.dateOfBirth}</i></b></p>
                                <p>Địa chỉ:&emsp; <b><i>{ok.address}</i></b></p>
                            </div>
                        ))
                    }
                </div>
            </div>
        </div>

    )
}

Chitiettaikhoan.propTypes = {

}

export default Chitiettaikhoan
