import { message } from "antd";
import axiosClient from "./axiosClient";

class ResponsecommentApi {
    getAll = (params) => {
        const url = '/responsecomments';
        return axiosClient.get(url, { params });
    };
    postresponsecomment = (params) => {
        const url = '/responsecomments';
        return axiosClient.post(url, params).then(data => {
            message.success("Đặt vé thành công!");
        }).catch(err => {
            message.error("Có lỗi xảy ra!");
        });
    };
    deleteresponsecomment = (id) => {
        const url = `/responsecomments/${id}`;
        return axiosClient.delete(url).then(data => {
            message.success("Xoá thành công!");
        }).catch(err => {
            message.error("Có lỗi xảy ra!");
        });
    };
    editresponsecomment = (params) => {
        const url = `/responsecomments/${params.idsua}`;
        return axiosClient.patch(url, params).then(data => {
            message.success("Sửa thành công!");
        }).catch(err => {
            message.error("Có lỗi xảy ra!");
        });
    }
}
const responsecommentApi = new ResponsecommentApi();
export default responsecommentApi;
