import { Button } from '@material-ui/core'
import { message } from 'antd';
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { useHistory, useParams } from 'react-router-dom';
import { phanhoiData, updatephanhoi } from './phanhoiSlice';

function Suaphanhoi() {
    const { id } = useParams();
    const dispatch = useDispatch();
    const [state, setState] = useState({ analyzeComment: '', responseComment: '' });
    const onChange = e => {
        setState({
            ...state,
            [e.target.name]: e.target.value,
        });
    }
    const actionResult = async () => { await dispatch(phanhoiData()) }
    const history = useHistory()
    const phanhoi = useSelector(state => state.phanhois.phanhoi.data.find(x => x.id === +id));
    useEffect(() => {
        if (id) {
            setState({
                analyzeComment: phanhoi.analyzeComment,
                responseComment: phanhoi.responseComment,
                idsua: id
            })
        }
    }, [])
    const { analyzeComment, responseComment } = state;
    const onSubmit = e => {
        e.preventDefault();
        if (analyzeComment.trim() === '' || analyzeComment.trim() === "") {
            message.error("Xin hãy nhập đầy đủ thông tin!");
        } else {
            if (id) {
                dispatch(updatephanhoi(state));
            } else {
                return;
            }
            setTimeout(() => {
                actionResult();
            }, 700);
            history.push("/admin/phanhoi");
        }
    }
    return (
        <div id="admin">
            <div className="heading">
                <h4>{id ? "Sửa dịch vụ" : "Thêm dịch vụ"}</h4>
                <div className="hr"></div>
            </div>
            <div className="content">
                <form action="" method="post" onSubmit={onSubmit}>
                    <div className="form-group">
                        <label htmlFor="">Tên dịch vụ</label>
                        <input type="text" name="name" value={name} onChange={onChange} className="form-control w-50" placeholder="" aria-describedby="helpId" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="">Icon</label>
                        <input type="text" name="icon" value={icon} onChange={onChange} className="form-control w-50" placeholder="" aria-describedby="helpId" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="">Mô tả dịch vụ</label>
                        <textarea name="mota" onChange={onChange} value={mota} cols="30" className="form-control w-50" rows="10"></textarea>
                    </div>
                    <div className="text-center mtb"><Button type="submit" color="primary" variant="contained">{id ? "Sửa dịch vụ" : "Thêm dịch vụ"}</Button></div>
                </form>
            </div>
        </div>
    )
}

Suaphanhoi.propTypes = {

}

export default Suaphanhoi
