import hoadonApi from "../../../../api/phanhoiApi";

const { createSlice, createAsyncThunk } = require("@reduxjs/toolkit");

export const phanhoiData = createAsyncThunk('phanhois/phanhoiData', async () => {
    const phanhoi = await phanhoiApi.getAll();
    return phanhoi;
})
const Phanhoi = createSlice({
    name: "phanhois",
    initialState: {
        phanhoi: [],
        loading: true,
        error: ''
    },
    reducers: {
        addhoadon: (state, action) => {
            phanhoiApi.posthoadon(action.payload);
        },
        removehoadon: (state, action) => {
            phanhoiApi.deletehoadon(action.payload);
        },
        updatehoadon: (state, action) => {
            phanhoiApi.edithoadon(action.payload);
        }
    },
    extraReducers: {
        [phanhoiData.pending]: (state) => {
            state.loading = true;
        },
        [phanhoiData.rejected]: (state, action) => {
            state.loading = true;
            state.error = action.error;
        },
        [phanhoiData.fulfilled]: (state, action) => {
            state.loading = false;
            state.phanhoi = action.payload;
        }
    }
});
const { reducer, actions } = Phanhoi;
export const { addhoadon, removehoadon, updatehoadon } = actions;

export default reducer;
